using System;namespace Utilities.Courses {public partial class QHelper : IQHelper{public static string Q1(Random random, Action<string, ushort> registerAnswer, bool isProof){var q = Quest_Q1(random, isProof);string rval = q.GetQuestion(registerAnswer);return rval;}public static QuestionBase Quest_Q1(Random random, bool isProof) { var q = new XyzQuestion(random, isProof);q.Id = "Q1";q.Marks = 1; q.ShowMarks = false;q.Stem = @"<p>Which of these countries' flags does not have stars on it?</p>";q.AddCorrects(@"<p>South Korea</p>",@"<p>South Africa</p>",@"<p>Canada</p>");q.AddIncorrects(@"<p>USA</p>",@"<p>China</p>",@"<p>Vietnam</p>",@"<p>Isreal</p>",@"<p>Turkey</p>",@"<p>Chile</p>",@"<p>North Korea</p>");return q; }}}