using System;namespace Utilities.Courses {public partial class QHelper : IQHelper{public static string Q2(Random random, Action<string, ushort> registerAnswer, bool isProof){var q = Quest_Q2(random, isProof);string rval = q.GetQuestion(registerAnswer);return rval;}public static QuestionBase Quest_Q2(Random random, bool isProof) { var q = new TruthQuestion(random, isProof);q.Id = "Q2";q.Marks = 2; q.ShowMarks = false;q.Stem = @"<p>In which country was the <strong>2010</strong> 'Fifa World Cup' held?</p>";q.AddCorrects(@"<p>South Africa</p>");q.AddIncorrects(@"<p>France</p>",@"<p>Germany</p>",@"<p>Brazil</p>",@"<p>Russia</p>");return q; }}}